package com.dowhiletesting.practiceprogram;

public class TwentyDoWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int res=20;
		int i=1;
		do
		{
			res=i*20;
			String result="20*"+i+"="+res;
			
				System.out.println(result);
			
			i++;
		}
		 while(i<=10);

	}

	}


